import React, { useState } from 'react'

export default function Contact() {
  const [form, setForm] = useState({ name: '', email: '', message: '' })
  const [submitted, setSubmitted] = useState(false)

  function handleChange(e) {
    setForm({ ...form, [e.target.name]: e.target.value })
  }

  function handleSubmit(e) {
    e.preventDefault()
    // For now just simulate submit
    setSubmitted(true)
  }

  if (submitted) {
    return (
      <section
        id="contact"
        className="p-12 bg-blue-700 text-white text-center rounded max-w-xl mx-auto"
      >
        <h2 className="text-3xl font-bold mb-4">
          Thank you for contacting us!
        </h2>
        <p>We will get back to you shortly.</p>
      </section>
    )
  }

  return (
    <section
      id="contact"
      className="p-12 bg-white text-gray-900 max-w-xl mx-auto rounded shadow"
    >
      <h2 className="text-4xl font-bold mb-8 text-center">Contact Us</h2>
      <form onSubmit={handleSubmit} className="flex flex-col space-y-6">
        <input
          className="p-3 border border-gray-300 rounded"
          type="text"
          name="name"
          placeholder="Your Name"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          className="p-3 border border-gray-300 rounded"
          type="email"
          name="email"
          placeholder="Your Email"
          value={form.email}
          onChange={handleChange}
          required
        />
        <textarea
          className="p-3 border border-gray-300 rounded"
          name="message"
          rows="5"
          placeholder="Your Message"
          value={form.message}
          onChange={handleChange}
          required
        />
        <button
          type="submit"
          className="bg-blue-700 text-white font-bold py-3 rounded hover:bg-blue-800"
        >
          Send Message
        </button>
      </form>
    </section>
  )
}
