import React, { useState, useEffect } from 'react'

export default function Navbar() {
  const [isScrolled, setIsScrolled] = useState(false)
  const [menuOpen, setMenuOpen] = useState(false)

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) setIsScrolled(true)
      else setIsScrolled(false)
    }
    window.addEventListener('scroll', handleScroll)
    return () => window.removeEventListener('scroll', handleScroll)
  }, [])

  return (
    <header
      className={`fixed w-full top-0 left-0 z-50 transition-shadow bg-white ${
        isScrolled ? 'shadow-lg' : 'shadow-none'
      }`}
    >
      <nav className="max-w-7xl mx-auto px-6 py-4 flex justify-between items-center">
        {/* Logo/Brand */}
        <a
          href="/"
          className="text-2xl font-bold text-blue-700 hover:text-blue-900 transition"
        >
          neXetlab
        </a>

        {/* Desktop Menu */}
        <ul className="hidden md:flex space-x-8 text-gray-700 font-medium">
          {['Home', 'Services', 'About', 'Contact'].map((item) => (
            <li key={item}>
              <a
                href={`/${item.toLowerCase()}`}
                className="hover:text-blue-700 transition-colors"
              >
                {item}
              </a>
            </li>
          ))}
        </ul>

        {/* Mobile Hamburger */}
        <button
          onClick={() => setMenuOpen(!menuOpen)}
          className="md:hidden focus:outline-none"
          aria-label="Toggle Menu"
        >
          <svg
            className="w-7 h-7 text-blue-700"
            fill="none"
            stroke="currentColor"
            strokeWidth={2}
            viewBox="0 0 24 24"
          >
            {menuOpen ? (
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M6 18L18 6M6 6l12 12"
              />
            ) : (
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                d="M4 6h16M4 12h16M4 18h16"
              />
            )}
          </svg>
        </button>
      </nav>

      {/* Mobile Menu */}
      {menuOpen && (
        <ul className="md:hidden bg-white shadow-lg py-4 space-y-4 text-center font-medium text-gray-700">
          {['Home', 'Services', 'About', 'Contact'].map((item) => (
            <li key={item}>
              <a
                href={`/${item.toLowerCase()}`}
                onClick={() => setMenuOpen(false)}
                className="block px-4 py-2 hover:bg-blue-100 rounded transition"
              >
                {item}
              </a>
            </li>
          ))}
        </ul>
      )}
    </header>
  )
}
