import React, { useState, useRef } from 'react'
import emailjs from '@emailjs/browser'

export default function Contact() {
  const formRef = useRef()
  const [submitted, setSubmitted] = useState(false)
  const [error, setError] = useState(false)
  const [isSending, setIsSending] = useState(false)

  const sendEmail = (e) => {
    e.preventDefault()
    setIsSending(true)
    setError(false)

    emailjs
      .sendForm(
        'service_kd8b1m8', // Replace with your EmailJS service ID
        'template_ya2qcv6', // Replace with your EmailJS template ID
        formRef.current,
        'D-9hxmFbirQt7Sgof' // Replace with your EmailJS public key
      )
      .then(
        () => {
          setSubmitted(true)
          setIsSending(false)
        },
        () => {
          setError(true)
          setIsSending(false)
        }
      )
  }

  if (submitted) {
    return (
      <section className="p-12 bg-green-100 text-green-800 text-center rounded max-w-xl mx-auto shadow">
        <h2 className="text-3xl font-bold mb-2">Thanks for reaching out!</h2>
        <p className="text-lg">Weâ€™ll be in touch with you very soon.</p>
      </section>
    )
  }

  return (
    <section className="max-w-xl mx-auto p-8 bg-white rounded-xl shadow-lg">
      <h2 className="text-4xl font-bold mb-6 text-blue-700 text-center">
        Contact Us
      </h2>
      <form
        ref={formRef}
        onSubmit={sendEmail}
        className="flex flex-col space-y-5"
      >
        <input
          type="text"
          name="user_name"
          placeholder="Your Name"
          required
          className="p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-600"
        />
        <input
          type="email"
          name="user_email"
          placeholder="Your Email"
          required
          className="p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-600"
        />
        <textarea
          name="message"
          placeholder="Your Message"
          rows="5"
          required
          className="p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-600 resize-none"
        />
        <button
          type="submit"
          disabled={isSending}
          className={`bg-blue-700 text-white font-bold py-3 rounded hover:bg-blue-800 transition ${
            isSending ? 'opacity-50 cursor-not-allowed' : ''
          }`}
        >
          {isSending ? 'Sending...' : 'Send Message'}
        </button>

        {error && (
          <p className="text-red-600 text-sm mt-2 text-center">
            Failed to send message. Please try again.
          </p>
        )}
      </form>
    </section>
  )
}
