import React from 'react'

const services = [
  {
    title: 'Software Development',
    description: 'Custom software solutions tailored to your business needs.',
    image: 'https://img.icons8.com/ios-filled/100/0000ff/source-code.png',
  },
  {
    title: 'Website Development',
    description:
      'Modern and responsive websites to boost your online presence.',
    image: 'https://img.icons8.com/ios-filled/100/0000ff/domain.png',
  },
  {
    title: 'Landing Pages',
    description:
      'High-converting landing pages designed for marketing campaigns.',
    image: 'https://img.icons8.com/ios-filled/100/0000ff/landing-page.png',
  },
]

export default function Services() {
  return (
    <section>
      <h2 className="text-4xl font-bold text-center mb-12 text-blue-700">
        Our Services
      </h2>
      <div className="grid md:grid-cols-3 gap-10 max-w-6xl mx-auto">
        {services.map(({ title, description, image }) => (
          <div
            key={title}
            className="p-6 rounded-lg shadow-lg hover:shadow-xl transition cursor-pointer text-center"
          >
            <img src={image} alt={title} className="mx-auto mb-4 w-20 h-20" />
            <h3 className="text-2xl font-semibold mb-2">{title}</h3>
            <p className="text-gray-600">{description}</p>
          </div>
        ))}
      </div>
    </section>
  )
}
