import React from 'react'
import { Link } from 'react-router-dom' // or your preferred router
import { motion } from 'framer-motion'

export default function Navbar() {
  return (
    <nav className="bg-gradient-to-r from-purple-700 via-pink-600 to-red-600 shadow-lg fixed w-full z-50">
      <div className="max-w-7xl mx-auto px-8">
        <div className="flex items-center justify-between h-16">
          {/* Logo */}
          <motion.div
            initial={{ scale: 0.9 }}
            animate={{ scale: 1 }}
            transition={{ duration: 0.5 }}
          >
            <Link
              to="/"
              className="text-white text-2xl font-bold tracking-wide hover:text-yellow-300 transition"
            >
              neXetlab
            </Link>
          </motion.div>

          {/* Nav Links */}
          <div className="hidden md:flex space-x-8 text-white font-semibold text-lg">
            {['Home', 'Services', 'About', 'Contact'].map((item) => (
              <Link
                key={item}
                to={item === 'Home' ? '/' : `/${item.toLowerCase()}`}
                className="relative px-3 py-2 hover:text-yellow-300 transition"
              >
                <span className="relative z-10">{item}</span>
                <motion.span
                  className="absolute left-0 bottom-0 w-full h-0.5 bg-yellow-300 rounded"
                  layoutId="underline"
                  initial={{ scaleX: 0 }}
                  whileHover={{ scaleX: 1 }}
                  transition={{ duration: 0.3 }}
                  style={{ transformOrigin: 'left' }}
                />
              </Link>
            ))}
          </div>

          {/* Mobile Menu Placeholder */}
          <div className="md:hidden">
            {/* Add hamburger menu here if needed */}
          </div>
        </div>
      </div>
    </nav>
  )
}
